import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux';
import { detailsUser } from '../actions/UserActions';
function SigninScreen(props){
    const [qty ,setQty] = useState(1);
    const UserDetails = useSelector(state => state.UserDetails);
    const { User, loading, error } = UserDetails;
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(detailsUser(props.match.params.id));
        return () => {
            //
        };
    }, []);
    const handleAddCart = () =>{
        props.history.push("/cart/" + props.match.params.id + "?qty=" + qty)
    }
    return <div>
        <div className="back-to-result">
            <Link to="/">Back to Result</Link>
        </div>
        { loading? <div>Loading...</div>: 
        error? <div>{error}</div>:
        (
        <div className="details">
            <div className="details-image">
                <img src={User.image} alt="User" />
            </div>
            <div className="details-info">
                <ul>
                    <li>
                        <h4> {User.name} </h4>
                    </li>
                    <li>
                        {User.ratting} Stars ({User.numReviews} Reviews)
                    </li>
                    <li>
                       Price:<b>&#x09F3;{User.price} </b>
                    </li>
                    <li>Description:
                        <div>
                            {User.description}
                        </div>
                    </li>
                </ul>
            </div>
            <div className="details-action">
                <ul>
                    <li>
                        Price: {User.price}
                    </li>
                    <li>
                        Status: {User.countInStock>0 ? "In Stock" : "Unavailable"}
                    </li>
                    <li>
                        Qty: <select value={qty} onChange ={(e) => {setQty(e.target.value)}} >
                            {[...Array(User.countInStock).keys()].map(x=>
                                <option key={x + 1} value={x + 1} >{x + 1}</option>
                                )}
                        </select>
                    </li>
                    <li>
                        { User.countInStock>0 && <button onClick={handleAddCart} className="button primary">Add to Cart</button>
                        }                    
                    </li>
                </ul>
            </div>
        </div>
        )
        
    }
        
        
    </div>
}
export default SigninScreen;
